Insert into TNO.NEWS_ITEMS (RSN,ITEM_DATE,SOURCE,ITEM_TIME,SUMMARY,TITLE,TYPE,FRONTPAGESTORY,PUBLISHED,ARCHIVED,ARCHIVED_TO,RECORD_CREATED,RECORD_MODIFIED,STRING1,STRING2,STRING3,STRING4,STRING5,STRING6,STRING7,STRING8,STRING9,NUMBER1,NUMBER2,DATE1,DATE2,FILENAME,FULLFILEPATH,WEBPATH,THISJUSTIN,IMPORTEDFROM,EXPIRE_RULE,COMMENTARY,CONTENTTYPE,BINARYLOADED,LOADBINARY,EXTERNALBINARY,CBRA_NONQSM,POSTEDBY,ONTICKER,WAPTOPSTORY,ALERT,AUTO_TONE,CATEGORIES_LOCKED,CORE_ALERT,COMMENTARY_TIMEOUT,COMMENTARY_EXPIRE_TIME,EOD_CATEGORY,EOD_CATEGORY_GROUP,EOD_DATE) values (778281084,to_date('19-MAY-23','DD-MON-RR'),'Castanet',to_date('01-JAN-70','DD-MON-RR'),'The Thompson-Nicola Regional District board of directors have voted unanimously to ask the BC Wildfire Service to put a campfire ban in place throughout the region before the upcoming long weekend.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'In a meeting on Thursday, Ward Stamer, Barriere mayor and TNRD director, put forward the motion, noting pervasive hot and dry conditions and an early start to the wildfire season with fires already raging in northern B.C.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"I honestly believe we should be proactive, not reactive," Stamer said.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Stamer said a large number of the currently active wildfires have been caused by human activity, but the weekend forecast is also calling for thunderstorms and not a lot of rain.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"We really don''t need to take a chance," he said.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"Even if we haven''t got the temperatures as high as they''ve been over last few days, we''re going to have wind, we''re going to have thunderstorms, we could have lightning, we could have all the right mixes to have a catastrophic event. And with all the smoke out here, we wouldn''t know where the fire is until it''s too late."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'As of Thursday, burn piles and materials like fireworks and sky lanterns have been banned from the Kamloops Fire Centre, but category 1 campfires, which are a half-metre high by a half-metre wide, are still permitted.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Stamer''s motion will see an email sent to the wildfire agency strongly recommending a campfire ban be put in place for the portion of the Kamloops Fire Centre which falls within TNRD boundaries before the start of the May long weekend.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Other regional districts whose boundaries fall within the Kamloops Fire Centre will be copied on the communication.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Other directors agreed with Stamer''s recommendation, including Barbara Roden, Ashcroft mayor and TNRD board chair.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Roden said the Kamloops Fire Centre held off on declaring a campfire ban during the heat dome in 2021, which resulted in "considerable head-shaking" from locals.','TNRD directors vote unanimously to urge BCWS to ban campfires','Internet',1,1,0,null,to_date('19-MAY-23','DD-MON-RR'),to_date('19-MAY-23','DD-MON-RR'),null,null,null,'column','Castanet','Castanet',null,null,'SyndEnclosureImpl.length=110615' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.interface=interface com.sun.syndication.feed.synd.SyndEnclosure' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.type=image/jpeg' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.url=https://www.castanet.net/content/2023/5/img_6274_p3615827_p3687841.jpg',0,0,null,to_date('01-JAN-70','DD-MON-RR'),null,null,'https://www.castanet.net/news/Kamloops/427559/TNRD-directors-vote-unanimously-to-urge-BCWS-to-ban-campfires',0,null,0,0,null,0,0,0,0,'chall',0,0,0,null,0,0,0,0,null,null,null);
