Insert into TNO.NEWS_ITEMS (RSN,ITEM_DATE,SOURCE,ITEM_TIME,SUMMARY,TITLE,TYPE,FRONTPAGESTORY,PUBLISHED,ARCHIVED,ARCHIVED_TO,RECORD_CREATED,RECORD_MODIFIED,STRING1,STRING2,STRING3,STRING4,STRING5,STRING6,STRING7,STRING8,STRING9,NUMBER1,NUMBER2,DATE1,DATE2,FILENAME,FULLFILEPATH,WEBPATH,THISJUSTIN,IMPORTEDFROM,EXPIRE_RULE,COMMENTARY,CONTENTTYPE,BINARYLOADED,LOADBINARY,EXTERNALBINARY,CBRA_NONQSM,POSTEDBY,ONTICKER,WAPTOPSTORY,ALERT,AUTO_TONE,CATEGORIES_LOCKED,CORE_ALERT,COMMENTARY_TIMEOUT,COMMENTARY_EXPIRE_TIME,EOD_CATEGORY,EOD_CATEGORY_GROUP,EOD_DATE) values (777758908,to_date('16-MAY-23','DD-MON-RR'),'iNFOnews',to_date('01-JAN-70','DD-MON-RR'),'A Kamloops woman who was attacked in a road rage incident says she has received no information from RCMP despite multiple calls to the detachment. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  Norma Bronson says she has left seven messages for the investigating officer and hopes to get a restraining order against her attacker since the incident on May 1. No one has called her back, she says. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  &quot;I have not been able to talk to anybody and I don&#39;t understand that,&quot; Bronson said. &quot;I would like to touch base, and they should just remember that I&#39;m a human being that was attacked by another person and it&#39;s not okay.&quot; ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  Charges have not been forwarded to Crown prosecutors and detachment spokesperson Cpl. Crystal Evelyn said the investigation is still ongoing.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || ' READ MORE:&nbsp;Tipster ID&#39;d Kamloops park assailant weeks ago; no response from RCMP ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  Bronson, 60, was attacked in a road rage incident after she was followed by an aggressive driver in North Kamloops. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  She said she was abiding by a construction zone speed limit when her attacker was weaving and trying to pass. He sped off once they were through the construction zone on Fortune Drive. She thought the incident was over and parked at a nearby grocery store, when the man ran towards her in the parking lot. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  She says he pushed her down, kicked her, then spat on her.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || ' READ MORE:&nbsp;iN VIDEO: Kamloops residents brace for another year of &#39;ear-piercing&#39; construction ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  Bronson is now recovering and has seen multiple doctors to ensure she heals and doesn&#39;t need surgery, which was confirmed Monday, May 15. Meanwhile, her assailant is free in the community despite the quick response from Kamloops RCMP. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '  A bystander followed Bronson&#39;s assailant through the parking lot to ensure he didn&#39;t get away from police and officers managed to speak to the man. He was released with conditions. ','Kamloops road rage attack victim left in the dark by cops','Internet',1,1,0,null,to_date('16-MAY-23','DD-MON-RR'),to_date('16-MAY-23','DD-MON-RR'),null,null,null,'column','Levi Landry','Levi Landry',null,null,null,0,0,null,to_date('01-JAN-70','DD-MON-RR'),null,null,'https://infotel.ca/newsitem/kamloops-road-rage-attack-victim-left-in-the-dark-by-cops/it98361',0,null,0,0,null,0,0,0,0,'richard',0,0,0,null,0,0,0,0,null,null,null);
