Insert into TNO.NEWS_ITEMS (RSN,ITEM_DATE,SOURCE,ITEM_TIME,SUMMARY,TITLE,TYPE,FRONTPAGESTORY,PUBLISHED,ARCHIVED,ARCHIVED_TO,RECORD_CREATED,RECORD_MODIFIED,STRING1,STRING2,STRING3,STRING4,STRING5,STRING6,STRING7,STRING8,STRING9,NUMBER1,NUMBER2,DATE1,DATE2,FILENAME,FULLFILEPATH,WEBPATH,THISJUSTIN,IMPORTEDFROM,EXPIRE_RULE,COMMENTARY,CONTENTTYPE,BINARYLOADED,LOADBINARY,EXTERNALBINARY,CBRA_NONQSM,POSTEDBY,ONTICKER,WAPTOPSTORY,ALERT,AUTO_TONE,CATEGORIES_LOCKED,CORE_ALERT,COMMENTARY_TIMEOUT,COMMENTARY_EXPIRE_TIME,EOD_CATEGORY,EOD_CATEGORY_GROUP,EOD_DATE) values (778742517,to_date('22-MAY-23','DD-MON-RR'),'CBC Online',to_date('01-JAN-70','DD-MON-RR'),'<img src="https://i.cbc.ca/1.6849878.1684549156!/fileImage/httpImage/image.jpg_gen/derivatives/16x9_460/spectrum-community-school-marching-band.jpg" title="Students at the Spectrum Community School in Victoria rehearse for their performance at Monday''s Victoria Day Parade. " />' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Canada''s largest Victoria Day Parade is making a comeback to the holiday''s namesake city Monday, with international performers returning for the first time since before the COVID-19 pandemic. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Marching bands from the United States are returning to perform, a tradition the organizers say dates back decades but has been on hiatus since 2019. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"They are just overwhelmed with the excitement that they have," said Kelly Kurta from the Greater Victoria Festival Society. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Kurta expects turnout to return to pre-pandemic levels of between 70,000 and 75,000 people this year - the 123rd year of the parade. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'She says the marching bands play an instrumental role in the celebration, performing both outside the legislature building on Sunday and in the parade on Monday. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"It''s alive. It''s exciting. The energy and the precision and the accuracy that these bands have is phenomenal," said Kurta. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Fourteen bands will perform in total, including eight from the United States, from Washington, Oregon, and California. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"It really feels like we''re back. It feels like the city''s alive. I just want to open our arms and invite everyone from around the world to our city." ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'The Spectrum Community School''s marching band has been practising for the performance since the beginning of the year. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Grade 12 student Alistar Dhaul is excited to be marching in front of an audience again. ' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"An experience a lot of us enjoy is when we''re walking down Government Street, and you can hear the drums ricochet against the building," said Dhaul, who plays the flute. ','75,000 expected to attend as international performers return to Victoria Day parade','Internet',1,1,0,null,to_date('22-MAY-23','DD-MON-RR'),to_date('22-MAY-23','DD-MON-RR'),null,null,null,'SyndCategoryImpl.taxonomyUri=null SyndCategoryImpl.name=News/Canada/British Columbia ','CBC Online','CBC Online',null,null,null,0,0,null,to_date('01-JAN-70','DD-MON-RR'),null,null,'https://www.cbc.ca/news/canada/british-columbia/victoria-day-parade-band-1.6849639?cmp=rss',0,null,0,0,null,0,0,0,0,'tday',0,0,0,null,0,0,0,0,null,null,null);
