Insert into TNO.NEWS_ITEMS (RSN,ITEM_DATE,SOURCE,ITEM_TIME,SUMMARY,TITLE,TYPE,FRONTPAGESTORY,PUBLISHED,ARCHIVED,ARCHIVED_TO,RECORD_CREATED,RECORD_MODIFIED,STRING1,STRING2,STRING3,STRING4,STRING5,STRING6,STRING7,STRING8,STRING9,NUMBER1,NUMBER2,DATE1,DATE2,FILENAME,FULLFILEPATH,WEBPATH,THISJUSTIN,IMPORTEDFROM,EXPIRE_RULE,COMMENTARY,CONTENTTYPE,BINARYLOADED,LOADBINARY,EXTERNALBINARY,CBRA_NONQSM,POSTEDBY,ONTICKER,WAPTOPSTORY,ALERT,AUTO_TONE,CATEGORIES_LOCKED,CORE_ALERT,COMMENTARY_TIMEOUT,COMMENTARY_EXPIRE_TIME,EOD_CATEGORY,EOD_CATEGORY_GROUP,EOD_DATE) values (778442091,to_date('20-MAY-23','DD-MON-RR'),'Global News: BC 1',to_date('01-JAN-70','DD-MON-RR'),'Police in the North Okanagan say they''ll be patrolling the great outdoors and local recreation sites this May long weekend.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'The patrols, say Vernon North Okanagan RCMP, are part of its enhanced seasonal policing initiative.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Officers will be conducting education and enforcement activities in B.C. recreation campsites.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"The North Okanagan is home to some great backcountry areas," said Sgt. Justin Thiessen.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"We want everyone to take advantage of what this beautiful area has to offer. Our goal is to ensure that everyone is using these areas in a safe and responsible manner."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Items police will be checking include off-road vehicle use, impaired driving plus responsible camping and campfires.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"In previous years, these patrols have resulted in multiple impaired driving charges, charges under the Off-Road Vehicle Act, and even evictions from campsites," said the RCMP.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'If you observe unsafe behaviour, including impaired driving, you''re asked to report it to your local RCMP detachment.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"Police want to remind residents that intoxicating substances and off-road vehicles do not mix," said Thiessen.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"Impaired operation of off-road vehicles is as dangerous and can have fatal consequences. It also carries the same punishments under the Criminal Code. Whether it''s dirtbikes, ATVs, or watercraft, if you''re going to drink or consume intoxicating substances, please make the responsible decision and do not drive." [pssg]','RCMP in North Okanagan conducting backcountry patrols during long weekend','Internet',1,1,0,null,to_date('20-MAY-23','DD-MON-RR'),to_date('20-MAY-23','DD-MON-RR'),null,null,null,'column','Doyle Potenteau','Doyle Potenteau',null,null,null,0,60,null,to_date('01-JAN-70','DD-MON-RR'),null,null,'https://globalnews.ca/news/9713616/rcmp-north-okanagan-responsible-camping-may-long-weekend/',0,null,0,0,null,0,0,0,0,'astrating',0,0,0,null,0,0,0,0,null,null,null);
