Insert into TNO.NEWS_ITEMS (RSN,ITEM_DATE,SOURCE,ITEM_TIME,SUMMARY,TITLE,TYPE,FRONTPAGESTORY,PUBLISHED,ARCHIVED,ARCHIVED_TO,RECORD_CREATED,RECORD_MODIFIED,STRING1,STRING2,STRING3,STRING4,STRING5,STRING6,STRING7,STRING8,STRING9,NUMBER1,NUMBER2,DATE1,DATE2,FILENAME,FULLFILEPATH,WEBPATH,THISJUSTIN,IMPORTEDFROM,EXPIRE_RULE,COMMENTARY,CONTENTTYPE,BINARYLOADED,LOADBINARY,EXTERNALBINARY,CBRA_NONQSM,POSTEDBY,ONTICKER,WAPTOPSTORY,ALERT,AUTO_TONE,CATEGORIES_LOCKED,CORE_ALERT,COMMENTARY_TIMEOUT,COMMENTARY_EXPIRE_TIME,EOD_CATEGORY,EOD_CATEGORY_GROUP,EOD_DATE) values (777605382,to_date('15-MAY-23','DD-MON-RR'),'Castanet',to_date('01-JAN-70','DD-MON-RR'),'A Vancouver documentary film maker is bringing his latest film to Kelowna''s Mary Irwin Theatre later this month.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '''On a Scale of 1 to 10'' is the new film from the team behind ''Your Second Fifty,'' and ''To Life, Death and Beyond''.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'The film from Laurent Goldstein and executive producer Jennifer Unruh focuses on chronic pain and features Kelowna Doctor Grant Pagdin.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"The motivation to make the film is to show people a different path to regain their quality of life," said Goldstein. "While chronic pain in some cases might never be totally overcome, there are modalities, therapies, approaches, that can make a substantial difference in people''s lives."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'He said the film explores ways to treat pain beyond prescription opioids, over the counter pills, and surgery.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"It takes you to clinics and wellness centres that are treating pain patients using new diagnostic tools, innovative research and advanced products and therapies."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'The film follows patients suffering from chronic pain, and features interviews with some of the top experts in the field, including Dr. Bruce Lipton, Dr. Daniel Amen, and local Dr. Grant Pagdin, an M.D. who does stem cell rejuvenation work at the Pagdin clinic.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'According to Goldstein, chronic pain affects one out of five people in North America, and in extreme cases, some people can find themselves misunderstood, isolated and depressed.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"My hope is that people will come to the event, and leave with a smile on their face because they''ll see that there is hope for them."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'The movie took two and a half years to produce. Along the way, Goldstein says he discovered that for many, chronic pain is a result of past trauma.','BC filmmaker examines new ways to treat chronic pain and illness','Internet',1,1,0,null,to_date('15-MAY-23','DD-MON-RR'),to_date('15-MAY-23','DD-MON-RR'),null,null,null,'column','Castanet','Castanet',null,null,'SyndEnclosureImpl.length=41533' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.interface=interface com.sun.syndication.feed.synd.SyndEnclosure' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.type=image/jpeg' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.url=https://www.castanet.net/content/2023/5/goldstein_p3685070.jpg',0,0,null,to_date('01-JAN-70','DD-MON-RR'),null,null,'https://www.castanet.net/news/Kelowna/426109/BC-filmmaker-examines-new-ways-to-treat-chronic-pain-and-illness',0,null,0,0,null,0,0,0,0,'jglass',0,0,0,null,0,0,0,0,null,null,null);
