Insert into TNO.NEWS_ITEMS (RSN,ITEM_DATE,SOURCE,ITEM_TIME,SUMMARY,TITLE,TYPE,FRONTPAGESTORY,PUBLISHED,ARCHIVED,ARCHIVED_TO,RECORD_CREATED,RECORD_MODIFIED,STRING1,STRING2,STRING3,STRING4,STRING5,STRING6,STRING7,STRING8,STRING9,NUMBER1,NUMBER2,DATE1,DATE2,FILENAME,FULLFILEPATH,WEBPATH,THISJUSTIN,IMPORTEDFROM,EXPIRE_RULE,COMMENTARY,CONTENTTYPE,BINARYLOADED,LOADBINARY,EXTERNALBINARY,CBRA_NONQSM,POSTEDBY,ONTICKER,WAPTOPSTORY,ALERT,AUTO_TONE,CATEGORIES_LOCKED,CORE_ALERT,COMMENTARY_TIMEOUT,COMMENTARY_EXPIRE_TIME,EOD_CATEGORY,EOD_CATEGORY_GROUP,EOD_DATE) values (779220321,to_date('25-MAY-23','DD-MON-RR'),'Castanet',to_date('01-JAN-70','DD-MON-RR'),'Tk''emlups te Secwepemc Kukpi7 Rosanne Casimir says a band and City of Kamloops delegation that recently presented its final pitch for hosting the 2027 North American Indigenous Games gave the competition "a huge run for their money."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Last week, Casimir travelled to Nova Scotia as part of a group tasked with delivering an in-person presentation to the full North American Indigenous Games selection committee.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'The group included representatives from TteS and the City of Kamloops, including Mayor Reid Hamer-Jackson and Coun. Bill Sarai.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"I think the pitch went absolutely amazing," Casimir said.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"We put in our hearts, our soul, our spirits, and our passion for the importance of bringing the North American Indigenous Games here to the Tournament Capital of Canada."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Tk''emlups and the City of Kamloops are competing against Calgary to host the games, and Casimir said each presentation was held separately.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"We didn''t see their presentation, and they didn''t see our presentation either," Casimir said. "I do know that we had such a comprehensive package. It was just absolutely incredible."' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'The local delegation had one hour to give their presentation, showcasing Kamloops as a potential host community.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Sarai said Kamloops-Thompson-Cariboo MP Frank Caputo attended the presentation along with Murray Rankin, provincial minister of Indigenous relations and reconciliation, who brought a video from Premier David Eby and Lana Popham, tourism minister.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"We put up a strong presentation," Sarai said, adding this was the first time in his tenure as a city councillor that he''s seen four levels of government supporting each other on a common issue.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || '"It went a long way for reconciliation. It really made a difference," Sarai said.' || CHR(13) || CHR(10) || CHR(13) || CHR(10) || 'Casimir said the group talked about several key points, including available sporting venues, accommodations, and what the opening and closing ceremonies could look like.','Casimir says delegation made ''amazing'' final pitch to host 2027 Indigenous games','Internet',1,1,0,null,to_date('25-MAY-23','DD-MON-RR'),to_date('25-MAY-23','DD-MON-RR'),null,null,null,'column','Castanet','Castanet',null,null,'SyndEnclosureImpl.length=66799' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.interface=interface com.sun.syndication.feed.synd.SyndEnclosure' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.type=image/jpeg' || CHR(13) || CHR(10) || 'SyndEnclosureImpl.url=https://www.castanet.net/content/2023/5/339913989_945337613143893_2932470585887685113_n_p3677548_p3689272.jpg',0,0,null,to_date('01-JAN-70','DD-MON-RR'),null,null,'https://www.castanet.net/news/Kamloops/428416/Casimir-says-delegation-made-amazing-final-pitch-to-host-2027-Indigenous-games',0,null,0,0,null,0,0,0,0,'jglass',0,0,0,null,0,0,0,0,null,null,null);
