{
    "settings": {
        "width": 500,
        "height": 500,
        "chartType": "bar",
        "groupBy": "mediaType",
        "options": {
            "scales": {
                "x": {
                    "ticks": {
                        "stepSize": 1
                    }
                },
                "y": {
                    "ticks": {
                        "stepSize": 1
                    }
                }
            },
            "plugins": {
                "colors": {
                    "enabled": true
                },
                "legend": {
                    "title": {
                        "text": "",
                        "display": true
                    },
                    "display": true
                },
                "datalabels": {
                    "anchor": "center",
                    "labels": {
                        "title": {
                            "display": false
                        }
                    }
                }
            },
            "indexAxis": "x"
        }
    },
    "template": "@inherits RazorEngineCore.RazorEngineTemplateBase<TNO.Services.Reporting.Models.ChartEngineContentModel>\n@using System\n@using System.Linq\n@using TNO.Entities\n@{\n  var group = SectionSettings.GroupBy switch\n  {\n    \"mediaType\" => Content.GroupBy(c => c.MediaType?.Name).OrderBy(group => group.Key),\n    \"contentType\" => Content.GroupBy(c => c.ContentType.ToString()).OrderBy(group => group.Key),\n    \"byline\" => Content.GroupBy(c => c.Byline).OrderBy(group => group.Key),\n    \"series\" => Content.GroupBy(c => c.SeriesId.ToString()).OrderBy(group => group.Key),\n    \"sentiment\" => Content.GroupBy(c => c.TonePools.FirstOrDefault()?.Value.ToString() ?? \"0\").OrderBy(group => group.Key),\n    \"sentimentSimple\" => Content.GroupBy(c => {\n      var value = c.TonePools.FirstOrDefault()?.Value ?? 0;\n      if (value  < 0) return \"Negative\";\n      if (value > 0) return \"Positive\";\n      return \"Neutral\";\n    }).OrderBy(group => group.Key),\n    \"source\" => Content.GroupBy(c => c.OtherSource).OrderBy(group => group.Key),\n    \"dayMonthYear\" => Content.GroupBy(c => $\"{c.PublishedOn:dd-MM-yyyy}\").OrderBy(group => group.Key),\n    \"monthYear\" => Content.GroupBy(c =>$\"{c.PublishedOn:MM-yyyy}\").OrderBy(group => group.Key),\n    \"year\" => Content.GroupBy(c => $\"{c.PublishedOn:yyyy}\").OrderBy(group => group.Key),\n    _ =>  Content.GroupBy(c => c.OtherSource).OrderBy(group => group.Key),\n  };\n}\n{\n  \"labels\": [@String.Join(\",\", group .Select(c => $\"\\\"{c.Key}\\\"\"))],\n  \"datasets\": [\n    {\n      \"label\": \"Totals\",\n      \"data\":  [@String.Join(\", \", group .Select(c => c.Count()))]\n    }\n   ]\n}",
    "content": [],
    "linkedReportId": 57,
    "filter": {}
}

{
    "id": 0,
    "name": "test",
