    "template": "@inherits RazorEngineCore.RazorEngineTemplateBase<TNO.Services.Reporting.Models.ChartTemplateModel>\n@using System\n@using System.Linq\n@using TNO.Entities\n@{\n  var group = Settings.GroupBy switch\n  {\n    \"product\" => Content.GroupBy(c => c.Product?.Name).OrderBy(group => group.Key),\n    \"contentType\" => Content.GroupBy(c => c.ContentType.ToString()).OrderBy(group => group.Key),\n    \"byline\" => Content.GroupBy(c => c.Byline).OrderBy(group => group.Key),\n    \"series\" => Content.GroupBy(c => c.SeriesId.ToString()).OrderBy(group => group.Key),\n    \"sentiment\" => Content.GroupBy(c => c.TonePools.FirstOrDefault()?.Value.ToString() ?? \"0\").OrderBy(group => group.Key),\n    \"source\" => Content.GroupBy(c => c.OtherSource).OrderBy(group => group.Key),\n    _ =>  Content.GroupBy(c => c.OtherSource).OrderBy(group => group.Key),\n  };\n}\n{\n  \"labels\": [@String.Join(\",\", group .Select(c => $\"\\\"{c.Key}\\\"\"))],\n  \"datasets\": [\n    {\n      \"label\": \"Totals\",\n      \"data\":  [@String.Join(\", \", group .Select(c => c.Count()))]\n    }\n   ]\n}",
    "content": [],
    "filter": {}
}

{
    "templateId": 5,
    "template": {
        "subject": "@using TNO.TemplateEngine\n@Settings.Subject.Text @(Settings.Subject.ShowTodaysDate ? $\" - {ReportExtensions.GetTodaysDate():dd-MMM-yyyy}\" : \"\")",
        "body": "@inherits RazorEngineCore.RazorEngineTemplateBase<TNO.TemplateEngine.Models.Reports.ReportTemplateModel>\n@using System\n@using System.Linq\n@using TNO.Entities\n@using TNO.TemplateEngine\n@{\n  var pageBreak = Settings.Sections.UsePageBreaks ? \"page-break-after: always;\" : \"\";\n  var utcOffset = ReportExtensions.GetUtcOffset(System.DateTime.Now, \"Pacific Standard Time\");\n}\n<h2 id=\"top\">Report Title</h2>\n<div style=\"color:red;\">DO NOT FORWARD THIS EMAIL TO ANYONE</div>\n<br/>\n\n@if (Content.Count() == 0)\n{\n  <p>There is no content in this report.</p>\n}\n@if (Settings.ViewOnWebOnly)\n{\n  <a href=\"\">View report online</>\n}\nelse\n{\n  foreach (var section in Sections)\n  {\n    var sectionContent = section.Value.Content.ToArray();\n    if (section.Value.IsEnabled && sectionContent.Length > 0 || !Settings.Sections.HideEmpty)\n    {\n      <div style=\"@pageBreak\">\n        @if (!String.IsNullOrEmpty(section.Value.Settings.Label))\n          <h3>@section.Value.Settings.Label</h3>\n         @if (!String.IsNullOrEmpty(section.Value.Description))\n          <p>@section.Value.Description</p>\n        \n        @* Charts *@\n        @if (section.Value.Settings.ChartsOnTop && section.Value.Settings.ShowCharts)\n        {\n          foreach (var chart in section.Value.ChartTemplates)\n          {\n            <img alt=\"@chart.SectionSettings.AltText\" src=\"@chart.Uid\" />\n          }\n        }\n        \n        @* Table of Contents *@\n        @if (section.Value.Settings.ShowContent)\n        {\n          <ul>\n            @foreach (var content in sectionContent)\n            {\n              var headline = $\"{(Settings.Headline.ShowSentiment ? content.GetSentimentIcon(\"{0} - \") : \"\")}{content.Headline}{(Settings.Headline.ShowShortName && !String.IsNullOrEmpty(content.Source?.ShortName) ? $\" - {content.Source?.ShortName}\" : (Settings.Headline.ShowSource ? $\" - {content.OtherSource}\": \"\"))}{(Settings.Headline.ShowPublishedOn ? $\" - {content.PublishedOn?.AddHours(utcOffset):dd-MMM-yyyy}\" : \"\")}\";\n              if (Settings.Content.IncludeStory)\n              {\n                <li><a href=\"#@($\"item-{content.Id}\")\">@headline</a></li>\n              }\n              else\n              {\n                <li>@headline</li>\n              }\n            }\n          </ul>\n        \n          @* Full Stories *@\n          @if (Settings.Content.IncludeStory)\n          {\n            for (var i = 0; i < sectionContent.Length; i++)\n            {\n              var content = sectionContent[i];\n              var hasPrev = i > 0;\n              var prev = hasPrev ? sectionContent[i - 1].Id : 0;\n              var hasNext = (i + 1) < sectionContent.Length;\n              var next = hasNext ? sectionContent[i + 1].Id : 0;\n              \n              <div id=\"item-@content.Id\" style=\"display: flex; align-items: center;\">\n                <hr style=\"width: 100%; display: inline-block;\" />\n              </div>\n              <div>\n                <a href=\"#top\">top</a>\n                @if (i > 0) \n                {\n                  <a href=\"#item-@prev\">previous</a>\n                }\n                @if (i < sectionContent.Length -1) \n                {\n                  <a href=\"#item-@next\">next</a>\n                }\n              </div>\n              <h3>@content.Headline</h3>\n              <div>@content.Source?.Name</div>\n              <div>@content.PublishedOn?.AddHours(utcOffset).ToString(\"dddd, MMMM d, yyyy\")</div>\n              @if (!string.IsNullOrWhiteSpace(content.Page))\n              {\n                  <div>Page @content.Page</div>\n              }\n              @if (!string.IsNullOrWhiteSpace(content.Byline))\n              {\n                  <div>By @content.Byline</div>\n              }\n              <br />\n              <div>@content.GetBody()</div>\n            }\n            <hr style=\"width: 100%; display: inline-block;\" />\n          }\n        }\n        \n        @* Charts *@\n        @if (!section.Value.Settings.ChartsOnTop && section.Value.Settings.ShowCharts)\n        {\n          foreach (var chart in section.Value.ChartTemplates)\n          {\n            <img alt=\"@chart.SectionSettings.AltText\" src=\"@chart.Uid\" />\n          }\n        }\n      </div>\n    }\n  }\n}\n\n  \n  <p style=\"font-size: 9pt; margin-top: 0.5rem;\">\n    Terms of Use - This summary is a service provided by Government Communications and Public Engagement and is only intended for original addressee. All content is the copyrighted property of a third party creator of the material.\n    Copying, retransmitting, archiving, redistributing, selling, licensing, or emailing the material to any third party or any employee of the Province who is not authorized to access the material is prohibited.\n  </p>\n\n",
        "settings": {
            "enableSectionCharts": true,
            "enableSummaryCharts": true
        },
        "chartTemplates": [
            {
                "template": "@inherits RazorEngineCore.RazorEngineTemplateBase<TNO.Services.Reporting.Models.ChartTemplateModel>\n@using System\n@using System.Linq\n@using TNO.Entities\n@{\n  var group = SectionSettings.GroupBy switch \n  {\n    \"product\" => Content.GroupBy(c => c.Product?.Name).OrderBy(group => group.Key),\n    \"contentType\" => Content.GroupBy(c => c.ContentType.ToString()).OrderBy(group => group.Key),\n    \"byline\" => Content.GroupBy(c => c.Byline).OrderBy(group => group.Key),\n    \"series\" => Content.GroupBy(c => c.SeriesId.ToString()).OrderBy(group => group.Key),\n    \"sentiment\" => Content.GroupBy(c => c.TonePools.FirstOrDefault()?.Value.ToString() ?? \"0\").OrderBy(group => group.Key),\n    \"source\" => Content.GroupBy(c => c.OtherSource).OrderBy(group => group.Key),\n    _ =>  Content.GroupBy(c => c.OtherSource).OrderBy(group => group.Key),\n  };\n}\n{\n  \"labels\": [@String.Join(\",\", group .Select(c => $\"\\\"{c.Key}\\\"\"))],\n  \"datasets\": [\n    {\n      \"label\": \"Totals\",\n      \"data\":  [@String.Join(\", \", group .Select(c => c.Count()))]\n    }\n   ]\n}",
                "settings": {
                    "chartTypes": [
                        "bar",
                        "pie",
                        "line",
                        "doughnut",
                        "polarArea",
                        "radar"
                    ],
                    "groupBy": [
                        "contentType",
                        "byline",
                        "source",
                        "series",
                        "sentiment",
                        "product"
                    ],
                    "options": {
                        "scales": {
                            "x": {
                                "ticks": {
                                    "stepSize": 1
                                }
                            }
                        },
                        "plugins": {
                            "datalabels": {
                                "anchor": "center",
                                "labels": {
                                    "title": null
                                }
                            }
                        },
                        "indexAxis": "y"
                    }
                },
                "id": 1,
                "name": "Count",
                "description": "A chart with number of stories on the  x-axis.",
                "isEnabled": true,
                "sortOrder": 1,
                "createdOn": "2023-07-10T16:40:55.176143Z",
                "createdBy": "admin",
                "updatedOn": "2023-08-03T22:08:27.563191Z",
                "updatedBy": "admin",
                "version": 89
            },
            {
                "template": "@inherits RazorEngineCore.RazorEngineTemplateBase<TNO.Services.Reporting.Models.ChartTemplateModel>\n@using System\n@using System.Linq\n@using TNO.Entities\n@{\n // Get all dates in data\n  var dates = Content\n    .GroupBy(c => c.PublishedOn.HasValue ? new DateTime(c.PublishedOn!.Value.Year, c.PublishedOn!.Value.Month, c.PublishedOn!.Value.Day) : DateTime.MinValue)\n    .OrderBy(g=> g.Key)\n    .ToArray();\n\n  // Get all groups in data\n  var groups = Content\n    .GroupBy(\n      c => SectionSettings.GroupBy switch {\n        \"product\" => c.Product?.Name,\n        \"contentType\" => c.ContentType.ToString(),\n        \"byline\" => c.Byline,\n        \"series\" => c.Series?.Name,\n        \"sentiment\" => c.TonePools.FirstOrDefault()?.Value.ToString(),\n        \"source\" => c.OtherSource,\n        _ => c.OtherSource\n      },\n      c => c,\n      (k, c) => new { Label = k, Group = c.GroupBy(c => c.PublishedOn.HasValue ? new DateTime(c.PublishedOn!.Value.Year, c.PublishedOn!.Value.Month, c.PublishedOn!.Value.Day) : DateTime.MinValue) })\n    .OrderBy(g=> g.Label)\n    .ToArray();\n}\n{\n  \"labels\": [@String.Join(\",\", dates.Select(c => $\"\\\"{c.Key:dd-MMM}\\\"\"))],\n  \"datasets\": [\n    @for (var i = 0; i < groups.Length; i++)\n    {\n      //@(\"{}\")\n      @($\"{{ \\\"label\\\": \\\"{groups[i].Label}\\\", \\\"data\\\": [{@String.Join(\",\", dates.Select(d => groups[i].Group.Where(g => g.Key == d.Key).Count()))} ] }}\")\n      if (i < groups.Length-1) @(\",\")\n    }\n   ]\n}\n\n",
                "settings": {
                    "chartTypes": [
                        "bar",
                        "line",
                        "pie",
                        "doughnut",
                        "scatter",
                        "bubble",
                        "polarArea",
                        "radar"
                    ],
                    "groupBy": [
                        "source",
                        "contentType",
                        "byline",
                        "series",
                        "sentiment",
                        "product"
                    ],
